mapper.select:
select bdjl.*,dd.ddh,dd.sjxm,dd.cph,dd.lxlx,wz.mc wzMc,shbm.mc shbmMc,fhdw.mc fhdwMc,sygbjl.gbsj sygbsj,qygbjl.gbsj qygbsj from bang_dan_ji_lu bdjl left join ding_dan dd on bdjl.ddId=dd.id left join wu_zi wz on dd.wzId=wz.id left join shou_huo_bu_men shbm on dd.shbmId=shbm.id left join fa_huo_dan_wei fhdw on dd.fhdwId=fhdw.id left join (select * from guo_bang_ji_lu where gblx=1) sygbjl on dd.id=sygbjl.ddId left join (select * from guo_bang_ji_lu where gblx=2) qygbjl on dd.id=qygbjl.ddId where 1=1
and dd.ddh like CONCAT('%',#{ddh},'%')
order by bdjl.rq desc limit #{rowNum},#{rows}
