mapper.select:
select gbjl.*,dd.ddh,dd.cph from guo_bang_ji_lu gbjl,ding_dan dd where gbjl.qyDdId=dd.id

	and dd.ddh like CONCAT('%',#{ddh},'%')


	and dd.cph like CONCAT('%',#{cph},'%')


	and gbjl.gbsj between #{gbsjks} and #{gbsjjs}

order by gbjl.gbsj desc
limit #{rowNum},#{rows}

select count(*) from guo_bang_ji_lu gbjl left join ding_dan dd on gbjl.qyDdId=dd.id left join ding_dan_zhuang_tai ddzt on dd.ddztId=ddzt.id left join yun_shu_shang yss on dd.yssId=yss.id left join fa_huo_dan_wei fhdw on dd.fhdwId=fhdw.id left join shou_huo_bu_men shbm on dd.shbmId=shbm.id where ddzt.mc=#{ddztMc}
and dd.ddh like CONCAT('%',#{ddh},'%')
and dd.sjxm like CONCAT('%',#{sjxm},'%')
and dd.sjsfzh like CONCAT('%',#{sjsfzh},'%')
and dd.cph like CONCAT('%',#{cph},'%')
and yss.mc like CONCAT('%',#{yssMc},'%')
and fhdw.mc like CONCAT('%',#{fhdwMc},'%')
and shbm.mc like CONCAT('%',#{shbmMc},'%')
and gbjl.gbsj between #{gbsjks} and #{gbsjjs}
and gbjl.gblx=#{gblx}

select gbjl.*,dd.sjxm,dd.sjsfzh,dd.ddh,dd.cph,dd.lxlx,yss.mc yssMc,fhdw.mc fhdwMc,shbm.mc shbmMc from guo_bang_ji_lu gbjl left join ding_dan dd on gbjl.qyDdId=dd.id left join ding_dan_zhuang_tai ddzt on dd.ddztId=ddzt.id left join yun_shu_shang yss on dd.yssId=yss.id left join fa_huo_dan_wei fhdw on dd.fhdwId=fhdw.id left join shou_huo_bu_men shbm on dd.shbmId=shbm.id where ddzt.mc=#{ddztMc}
and dd.ddh like CONCAT('%',#{ddh},'%')
and dd.sjxm like CONCAT('%',#{sjxm},'%')
and dd.sjsfzh like CONCAT('%',#{sjsfzh},'%')
and dd.cph like CONCAT('%',#{cph},'%')
and yss.mc like CONCAT('%',#{yssMc},'%')
and fhdw.mc like CONCAT('%',#{fhdwMc},'%')
and shbm.mc like CONCAT('%',#{shbmMc},'%')
and gbjl.gbsj between #{gbsjks} and #{gbsjjs}
and gbjl.gblx=#{gblx}
order by gbjl.gbsj desc limit #{rowNum},#{rows}
