mapper.select:
select count(*) from zhi_jian_ji_lu zjjl,yong_hu zjy,ding_dan dd,ding_dan_zhuang_tai ddzt where zjjl.zjyId=zjy.id and zjjl.ddId=dd.id and dd.ddztId=ddzt.id

	and zjjl.jg=#{jg}


	and dd.ddh like CONCAT('%',#{ddh},'%')


	and dd.ddztId=#{ddztId}


	and dd.ddh like CONCAT('%',#{zjyXm},'%')


	and zjy.zsxm like CONCAT('%',#{zjyZsxm},'%')

select zjjl.id,zjjl.jg,zjjl.zjsj,dd.ddh,zjy.zsxm zjyZsxm from zhi_jian_ji_lu zjjl,yong_hu zjy,ding_dan dd,ding_dan_zhuang_tai ddzt where zjjl.zjyId=zjy.id and zjjl.ddId=dd.id and dd.ddztId=ddzt.id

	and zjjl.jg=#{jg}


	and dd.ddh like CONCAT('%',#{ddh},'%')


	and dd.ddztId=#{ddztId}


	and dd.ddh like CONCAT('%',#{zjyXm},'%')


	and zjy.zsxm like CONCAT('%',#{zjyZsxm},'%')
